INSERT INTO `films` VALUES ('86', 'Tekken: Blood Vengeance', 'C&acirc;u chuyện l&agrave; cuộc đối đầu giữa tổ chức Mishima Zaibatsu - Jin Kazama. v&agrave; tổ chức G Corporation đối lập với Mishima Zaibatsu. Cả hai đều t&igrave;m kiếm th&ocirc;ng tin về một học sinh c&oacute; t&ecirc;n Shin Kamiya.cả hai b&ecirc;n quyết định c&agrave;i người v&agrave;o để tiếp cận Shin, Đại diện b&ecirc;n Jin l&agrave; Alisa v&agrave; b&ecirc;n Kamiya l&agrave; Xiaoyu, trong qu&aacute; tr&igrave;nh l&agrave;m nhiệm vụ hai người họ v&ocirc; t&igrave;nh trở th&agrave;nh bạn th&acirc;n của nhau.\r\n&lt;div&gt;Khi sự việc vỡ lỡ phải đứng trước một b&ecirc;n l&agrave; t&igrave;nh bạn v&agrave; một b&ecirc;n l&agrave; nhiệm vụ. Phải chọn ra sao phản bội tổ chức hay đi theo t&igrave;nh bạn...mời c&aacute;c bạn xem sẽ r&otilde;.&lt;/div&gt;', null, 'http://vuighe.net/tekken-blood-vengeance', '[6,5,4,9]', '2017-04-21 09:27:57', '2017-04-21 09:27:57');
INSERT INTO `films` VALUES ('87', 'Tales of Vesperia: The First Strike', 'Tales of Vesperia: The First Strike diễn ra trước những sự kiện trong tr&ograve; chơi c&ugrave;ng t&ecirc;n ph&aacute;t h&agrave;nh tr&ecirc;n hệ m&aacute;y PS3 v&agrave; XBOX360. Nội dung phim tập trung kể về thời gian Yuri Lowell (nv ch&iacute;nh của game) v&agrave;o thời gian tại ngũ trong lực lượg Hiệp Sĩ Ho&agrave;ng Gia trước khi anh rời bỏ n&oacute;. Nhiều nh&acirc;n vật trong game cũng xuất hiện trong movie như Flynn Scifo, Repede (l&uacute;c n&agrave;o c&ograve;n l&agrave; ch&uacute; c&uacute;n nhỏ), Rita Mordio, Estellise Sidos Heurassein v&agrave; Raven.', null, 'http://vuighe.net/tales-of-vesperia-the-first-strike', '[6,5,11]', '2017-04-21 09:27:59', '2017-04-21 09:27:59');
