INSERT INTO `films` VALUES ('121', 'Juubee Ninpuuchou', 'Phong kiến Nhật Bản &ndash; Thời kỳ của những nguy hiểm, &acirc;m mưu v&agrave; lừa lọc. Kibagami Juubee, một ninja v&ocirc; chủ phi&ecirc;u bạt đơn độc v&agrave; nhận l&agrave;m thu&ecirc; cho những kẻ c&oacute; v&agrave;ng hoặc một nguy&ecirc;n nh&acirc;n xứng đ&aacute;ng. Juubee đ&atilde; chạm tr&aacute;n một nh&oacute;m ninja qu&aacute;i vật v&agrave; cuộc sống lang thang của anh bắt đầu bị đe dọa.', 'http://i.imgur.com/fdLtv99.jpg', 'http://vuighe.net/juubee-ninpuuchou', '[8,10,12,11,4]', '2017-04-21 09:29:06', '2017-04-21 11:24:37');
INSERT INTO `films` VALUES ('122', 'Jigoku Sensei Nube', 'Nueno Mesuke,Hay c&ograve;n gọi l&agrave; &quot;Nube&quot;,\r\n l&agrave; một thầy gi&aacute;o nhưng c&ocirc;ng việc kh&ocirc;ng chỉ c&oacute; dạy học m&agrave; c&ograve;n rất nhiều việc kh&aacute;c... N&oacute;i nhiều mất hay, nhi&ecirc;u đ&oacute; th&ocirc;i.', 'http://i.imgur.com/k2o52m7.jpg', 'http://vuighe.net/jigoku-sensei-nube', '[8,1,2,11,4]', '2017-04-21 09:29:07', '2017-04-21 11:24:40');
