INSERT INTO `films` VALUES ('127', 'Kuro no Sumika: Chronus', 'Makoto Nakazono c&oacute; khả năng nh&igrave;n thấy thứ m&agrave; người kh&aacute;c kh&ocirc;ng thể thấy\r\n&lt;div&gt;Một ng&agrave;y c&aacute;i thứ &quot;kh&ocirc;ng ai thấy&quot; đ&oacute; tiếp cận c&ocirc; bạn thuở nhỏ của ch&agrave;ng ta v&agrave; rồi.....&lt;/div&gt;', 'http://i.imgur.com/Iw292vA.jpg', 'http://vuighe.net/kuro-no-sumika-chronus', '[3,9]', '2017-04-21 09:29:23', '2017-04-21 11:24:55');
