INSERT INTO `films` VALUES ('144', 'Hal', 'Sau c&aacute;i chết của HAL, robot Q01 đ&atilde; chuyển h&oacute;a th&agrave;nh HAL để đem lại nghị lực sống cho Kurumi, n&oacute;i chung xem th&igrave; biết...', 'http://i.imgur.com/zdMqL9j.jpg', 'http://vuighe.net/hal', '[8,9]', '2017-04-21 09:50:25', '2017-04-21 11:25:28');
INSERT INTO `films` VALUES ('145', 'Hakuouki', '&lt;p&gt;Dựa theo một tr&ograve; chơi tr&ecirc;n hệ m&aacute;y PS2 c&ugrave;ng t&ecirc;n.\r\n&lt;/p&gt;&lt;p&gt;Thời l&agrave; thời đại Mạc Phủ. Nơi l&agrave; Kinh Đ&ocirc; (Kyoto). Một c&acirc;u chuyện xoay quanh cuộc đời v&agrave; sự nghiệp chiến đấu của c&aacute;c th&agrave;nh vi&ecirc;n trực thuộc T&acirc;n Đảng-T&acirc;n Tuyển Đảng (Shinsengumi). Với chữ &ldquo;Th&agrave;nh&rdquo; th&ecirc;u tr&ecirc;n cờ x&iacute;, những kiếm sỹ T&acirc;n Đảng coi c&aacute;i chết nhẹ tựa l&ocirc;ng hồng, mục ti&ecirc;u của họ ch&iacute;nh l&agrave; bảo vệ trị an của một thời đại sắp sửa lụi t&agrave;n nhường bước cho Minh Trị thời đại. Một Okita Souji- đội trưởng đội 1, dũng m&atilde;nh nhất trong c&aacute;c đội trưởng d&ugrave; th&acirc;n mang căn bệnh lao phổi, một Hajime Satou- đội trưởng đội 3 cương trực xem c&aacute;i &aacute;c l&agrave; ngọn nguồn cần ti&ecirc;u diệt&hellip;&nbsp;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;letter-spacing: 0.1px;&quot;&gt;Một c&ocirc; b&eacute; t&ecirc;n Yukimura Chizuru tr&ecirc;n con đường t&igrave;m kiếm cha m&igrave;nh đ&atilde; lạc v&agrave;o T&acirc;n Đảng v&agrave; c&ugrave;ng chiến đấu với những kiếm sỹ ấy. Tại sao lại c&oacute; hiện tượng h&oacute;a quỷ? c&aacute;c chiến sỹ T&acirc;n đảng đang giấu giếm điều g&igrave;? 1 c&acirc;u chuyện giả tưởng giữa 1 đội qu&acirc;n c&oacute; thật trong lịch sử Mạc Phủ.&lt;/span&gt;&lt;/p&gt;', 'http://i.imgur.com/xPdpeCT.jpg', 'http://vuighe.net/hakuouki', '[6,12,4]', '2017-04-21 09:50:26', '2017-04-21 11:25:30');
INSERT INTO `films` VALUES ('146', 'Mahou Shoujo Nante Mou Ii Desukara', 'Yuzuka Hanami được tiếp cận bởi một sinh vật b&iacute; ẩn m&agrave; n&oacute;i với c&ocirc;, c&ocirc; c&oacute; dấu hiệu của việc trở th&agrave;nh một c&ocirc; g&aacute;i ph&eacute;p thuật. Tuy nhi&ecirc;n, Yuzuka dạng huyền diệu c&oacute; c&ocirc; mặc một bộ đồ bơi.', 'http://i.imgur.com/Jm34MOs.jpg', 'http://vuighe.net/mahou-shoujo-nante-mou-ii-desukara', '[1]', '2017-04-21 09:50:28', '2017-04-21 11:25:32');
INSERT INTO `films` VALUES ('147', 'Giovanni no Shima', 'H&ograve;n đảo qu&ecirc; hương của Junpei, Kanta bị người Nga chiếm. Rồi &quot;họ&quot; trở về sau 50 năm xa xứ. C&oacute; một ch&uacute;t g&igrave; đ&oacute; giống với Mộ Đom Đ&oacute;m nhưng movie n&agrave;y happy ending.\r\n&lt;div&gt;&lt;br&gt;&lt;/div&gt;', 'http://i.imgur.com/aiHLPaB.jpg', 'http://vuighe.net/giovanni-no-shima', '[3]', '2017-04-21 09:50:30', '2017-04-21 11:25:35');
INSERT INTO `films` VALUES ('148', 'Soul Buster', 'Tại Kinh Ch&acirc;u, Trung Quốc. \r\n&lt;div&gt;Một cuốn s&aacute;ch lịch sử ghi ch&eacute;p về thời đại Tam Quốc viết rằng, nơi đ&acirc;y đ&atilde; từng l&agrave; một v&ugrave;ng đất chiến tranh bất tận.\r\n&lt;/div&gt;&lt;div&gt;Một nam sinh Cao Trung t&ecirc;n T&ocirc;n Thần, vốn gh&eacute;t lịch sử về nơi m&agrave; cậu sinh sống, đ&atilde; gặp v&agrave;i vấn đề về 1 giấc mơ nhất định.\r\n&lt;/div&gt;&lt;div&gt;Sau khi tỉnh giấc khỏi giấc mơ, những việc lạ l&ugrave;ng lần lượt diễn ra, khi chưa kịp nắm bắt vấn đề, th&igrave; cậu đột nhi&ecirc;n đ&atilde; phải đối mặt với Di&ecirc;n (Ngụy Di&ecirc;n), một trong những vị tưởng từ thời Tam Quốc, người đang c&oacute; &yacute; định giết cậu ta!\r\n&lt;/div&gt;&lt;div&gt;Ngay trước khi Di&ecirc;n vung thanh đại đao của m&igrave;nh xuống, một mỹ nh&acirc;n kỳ b&iacute; đ&atilde; xuất hiện trước mặt T&ocirc;n Thần.\r\n&lt;/div&gt;&lt;div&gt;Thử th&aacute;ch g&igrave; đang đ&oacute;n chờ T&ocirc;n Thần, khi cậu phải chứng kiến một trận đ&aacute;nh kinh ho&agrave;ng giữa 2 vị tướng từ thời Tam Quốc?!&lt;/div&gt;', 'http://i.imgur.com/OXKzD9j.jpg', 'http://vuighe.net/soul-buster', '[6,12]', '2017-04-21 09:50:32', '2017-04-21 11:25:36');
