INSERT INTO `films` VALUES ('164', 'Berserk: Ougon Jidai-hen III - Kourin', '&lt;p&gt;Phần cuối của loạt Movie Berserk: Thời Đại Ho&agrave;ng Kim. Cuộc giải cứu Griffith. Sự kiện NTR  Nhật Thực lịch sử. Mở ra thời Đại Đen Tối.&lt;/p&gt;&lt;p&gt;P/s: Phim c&oacute; những cảnh NTR Nhạy Cảm, Bạo Lực Nặng kh&ocirc;ng ph&ugrave; hợp với trẻ vị th&agrave;nh ni&ecirc;n. Kh&aacute;n giả n&ecirc;n c&acirc;n nhắc trước khi xem. Với kh&aacute;n giả dưới 18 tuổi, h&atilde;y xem c&ugrave;ng phụ huynh để c&oacute; những chỉ dẫn kỹ c&agrave;ng nhất.&lt;/p&gt;', null, 'http://vuighe.net/berserk-ougon-jidai-hen-iii-kourin', '[6,11,4]', '2017-04-21 09:51:03', '2017-04-21 09:51:03');
INSERT INTO `films` VALUES ('165', 'Berserk: Ougon Jidai-hen II - Doldrey Kouryaku', 'Phần 2 của loạt movie Berserk. Kể về cuộc c&ocirc;ng th&agrave;nh Doldrey của vương quốc Midland. Sự ra đi của Guts, khởi đầu cho sự kiện Nhật Thực v&agrave;o phần 3.', null, 'http://vuighe.net/berserk-ougon-jidai-hen-ii-doldrey-kouryaku', '[6,11,4]', '2017-04-21 09:51:05', '2017-04-21 09:51:05');
INSERT INTO `films` VALUES ('166', 'Berserk: Ougon Jidai-hen I - Haou no Tamago', 'Một số phận nghiệt ng&atilde;, một định mệnh t&agrave;n khốc v&agrave; một lời ti&ecirc;n đo&aacute;n về c&aacute;i chết kh&ocirc;ng thể tr&aacute;nh khỏi. Nh&acirc;n vật ch&iacute;nh Guts đ&atilde; phải chiến đấu trong một thế giới đi&ecirc;n cuồng để gi&agrave;nh giật hi vọng sống.\r\n&lt;div&gt;P/s: Phim c&oacute; nhiều cảnh bạo lực kh&ocirc;ng ph&ugrave; hợp với những ai muốn l&agrave;m con ngoan tr&ograve; giỏi.&lt;/div&gt;', null, 'http://vuighe.net/berserk-ougon-jidai-hen-i-haou-no-tamago', '[6,11,4]', '2017-04-21 09:51:07', '2017-04-21 09:51:07');
INSERT INTO `films` VALUES ('167', 'Bayonetta: Bloody Fate', 'Ngày nay, m&ocirc;̣t ma nữ t&ecirc;n là Enzo đã k&ecirc;́t giao với Bayonetta, sau 20 năm ngủ say trong dòng nước th&acirc;̀n và kh&ocirc;ng h&ecirc;̀ có m&ocirc;̣t ký ức nào. Sở hữu 1 nửa Con Mắt Tr&acirc;̀n Tục, Bayonetta đã đ&ecirc;́n Vigrid khi Enzo nói rằng đã tìm th&acirc;́y nửa con mắt còn lại ở đó.', null, 'http://vuighe.net/bayonetta-bloody-fate', '[6,4]', '2017-04-21 09:51:09', '2017-04-21 09:51:09');
INSERT INTO `films` VALUES ('168', 'Aura: Maryuuin Kouga Saigo no Tatakai', 'Ichirou Satou l&agrave; một học sinh trung học với ảo tưởng về người h&ugrave;ng mang t&ecirc;n Maryuuin Kouga. Cuộc sống của anh ch&agrave;ng bị thay đổi khi gặp một c&ocirc; g&aacute;i b&iacute; ẩn...', null, 'http://vuighe.net/aura-maryuuin-kouga-saigo-no-tatakai', '[8,1,2,4]', '2017-04-21 09:51:11', '2017-04-21 09:51:11');
