INSERT INTO `films` VALUES ('181', 'Shuumatsu no Izetta', '&lt;p&gt;V&agrave;o năm 1939 SCN, đế quốc Đức x&acirc;m lược c&aacute;c quốc gia l&aacute;ng giềng. Cuộc chiến nhanh ch&oacute;ng lan rộng cả Ch&acirc;u &Acirc;u v&agrave; thời đại thế chiến bắt đầu. V&agrave;o năm 1940, Đức chuyển hướng tấn c&ocirc;ng v&agrave;o vương quốc Eylstadt, một đất nước nhỏ b&eacute; ở v&ugrave;ng n&uacute;i Alps bao quanh bởi những con s&ocirc;ng v&agrave; rừng c&acirc;y. C&acirc;u chuyện xoay quanh Izetta, th&agrave;nh vi&ecirc;n duy nhất c&ograve;n sống của gia tộc ph&ugrave; thủy, Izetta nguyện gi&uacute;p đỡ c&ocirc;ng ch&uacute;a Fine v&agrave; đất nước Eylstadt trong trận chiến với đế quốc Đức.&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;C&aacute;c bạn xem th&ocirc;ng tin chi tiết của bộ anime tại đ&acirc;y nh&eacute;&lt;a href=&quot; http://tinanime.com/shuumatsu-no-izetta-anh-sang-hi-vong-trong-khoi-lua-chien-tranh&quot; target=&quot;_blank&quot;&gt; http://tinanime.com/shuumatsu-no-izetta-anh-sang-hi-vong-trong-khoi-lua-chien-tranh&lt;/a&gt;&lt;/p&gt;', null, 'http://vuighe.net/shuumatsu-no-izetta', '[6]', '2017-04-21 09:51:35', '2017-04-21 09:51:35');
