INSERT INTO `films` VALUES ('187', 'Mahou Shoujo Ikusei Keikaku', '&lt;p&gt;Trong thế giới nơi m&agrave; một trực tuyến được gọi l&agrave; Magical Girl Raising Project tạo ra cơ hội duy nhất cho 1 trong h&agrave;ng chục ng&agrave;n người chơi c&oacute; thể trở th&agrave;nh &ldquo;c&ocirc; g&aacute;i ph&eacute;p thuật&rdquo; &ndash; sở hữu khả năng phi thường về cả thể chất v&agrave; ngoại h&igrave;nh, cũng như sức mạnh ma thuật v&ocirc; c&ugrave;ng đặc biệt. Nhưng v&agrave;o một ng&agrave;y nọ, trong một khu vực bao gồm tới 16 c&ocirc; g&aacute;i ph&eacute;p thuật, nh&agrave; ph&aacute;t triển đ&atilde; t&ugrave;y tiện th&ocirc;ng b&aacute;o rằng cần phải giảm đi một nửa số lượng c&aacute;c c&ocirc; g&aacute;i ph&eacute;p thuật đ&oacute; để giải quyết vấn đề về năng lượng. L&uacute;c đầu, cuộc đua của 16 c&ocirc; g&aacute;i để thu thập th&ecirc;m &ldquo;kẹo ma thuật&rdquo; c&ograve;n quan trọng hơn đối thủ cạnh tranh với họ, nhưng c&aacute;c nguy&ecirc;n tắc đ&oacute; ngay lập tức bị ph&aacute; vỡ, v&agrave; ch&uacute;ng nhanh ch&oacute;ng trở th&agrave;nh một trận chiến giết người lẫn nhau để tồn tại trong thế giới n&agrave;y.&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;C&aacute;c bạn xem th&ocirc;ng tin chi tiết tại đ&acirc;y nh&eacute; &lt;a href=&quot;http://tinanime.com/mahou-shoujo-ikusei-keikaku-cuoc-chien-sinh-ton-giua-nhung-co-gai-phep-thuat/&nbsp;&quot; target=&quot;_blank&quot;&gt;http://tinanime.com/mahou-shoujo-ikusei-keikaku-cuoc-chien-sinh-ton-giua-nhung-co-gai-phep-thuat/&nbsp;&lt;/a&gt;&lt;/p&gt;', null, 'http://vuighe.net/mahou-shoujo-ikusei-keikaku', '[6,4]', '2017-04-21 09:51:47', '2017-04-21 09:51:47');
