INSERT INTO `films` VALUES ('197', 'Drifters', '&lt;p&gt;Khi đang trong trận chiến với qu&acirc;n đội Sekigahara, Shimazu Toyohisa quyết định lấy đầu l&atilde;nh ch&uacute;a Ii Naomasa, nhưng ch&iacute;nh anh cũng bị thương nặng do những mũi gi&aacute;o đ&acirc;m xuy&ecirc;n người m&igrave;nh. Anh đi ra khỏi chiến trường trong t&igrave;nh trạng bị trọng thương v&agrave; chảy m&aacute;u, Toyohisa lại thấy m&igrave;nh bị dịch chuyển sang một h&agrave;nh lang trắng to&aacute;t c&oacute; nhiều c&aacute;nh cửa, tại đ&oacute; c&oacute; một người đ&agrave;n &ocirc;ng đeo k&iacute;nh đang ngồi đợi anh. Người đ&agrave;n &ocirc;ng t&ecirc;n Murasaki đ&oacute; đ&atilde; gửi Toyohisa v&agrave;o c&aacute;nh cửa gần nhất v&agrave; chuyển anh đến thế giới kh&aacute;c. Tại đ&oacute;, Toyohisa gặp những chiến binh vĩ đại kh&aacute;c giống anh, ch&iacute;nh họ l&agrave; những người tạo n&ecirc;n đội &quot;Drifters&quot;.&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;C&aacute;c bạn xem th&ocirc;ng tin chi tiết tại đ&acirc;y nh&eacute; &lt;a href=&quot;http://tinanime.com/drifters-thien-truong-ca-cua-nhung-ke-luu-dat&quot; target=&quot;_blank&quot;&gt;http://tinanime.com/drifters-thien-truong-ca-cua-nhung-ke-luu-dat&lt;/a&gt;&lt;/p&gt;', null, 'http://vuighe.net/drifters', '[6]', '2017-04-21 09:52:05', '2017-04-21 09:52:05');
