INSERT INTO `films` VALUES ('257', 'Fudanshi Koukou Seikatsu', 'C&acirc;u chuyện kể về Sakaguchi l&agrave; ch&agrave;ng trai c&oacute; vẻ ngo&agrave;i tr&ocirc;ng b&igrave;nh thường như bao người kh&aacute;c, nhựng thực sự hắn ta l&agrave; một người cuồng BL (Fudanshi). Đ&acirc;y sẽ l&agrave; c&acirc;u chuyện kể về cuộc sống thường ng&agrave;y tại trường trung học với niềm đam m&ecirc; truyện Yaoi của ch&agrave;ng thanh ni&ecirc;n n&agrave;y.', null, 'http://vuighe.net/fudanshi-koukou-seikatsu', '[1,2,3]', '2017-04-21 09:54:03', '2017-04-21 09:54:03');
