INSERT INTO `films` VALUES ('315', '12-sai.: Chicchana Mune no Tokimeki', 'Câu chuyện kể về cô bé lớp sáu Hanabi và cùng vấn đề của tuổi mới lớn, bắt đầu với việc cô bé chứng kiến giáo viên chủ nhiệm của mình đang hôn nhau và lo rằng mình sẽ dậy thì trước các bạn cùng lứa. Bộ manga đã miêu tả chân thực những rung động đầu đời và sự ngây thơ trong sang của một c', 'http://i.imgur.com/K3t6410.jpg', 'http://anime47.com/phim/12-sai-chicchana-mune-no-tokimeki/m5553.html', '[]', '2017-04-21 09:58:17', '2017-04-21 12:39:25');
