INSERT INTO `films` VALUES ('345', 'Accel World [BD]', 'E hèm, như bài giới thiệu ở trên, “Accel World”, “Burst Linker”, là một trong những cụm từ English trong anime này. Trong ep 1 & 2, tớ đã có ghi chú tất cả Vietsub của những cụm từ này, vì trình độ trans có hạn + độ ảo của anime này quá tung chảo nên có lẽ sẽ có 1 vài sai sót, mong các bạn bỏ qua và góp ý để những ep sau mình trans tốt hơn. Chúc các bạn có những giây phút thẩm phim vui vẻ .', 'http://i.imgur.com/uNKu5jI.jpg', 'http://anime47.com/phim/accel-world/m2134.html', '[]', '2017-04-21 10:00:09', '2017-04-21 12:43:22');
