INSERT INTO `films` VALUES ('386', 'Ajin: Demi-Human', '\r\nMột ngày nọ, Kei Nagai, một học sinh cấp ba, gặp tai nạn, nhưng cậu ta vẫn sống. Hay nói cách khác, kei không hẳn là con người . Kể từ đó, thế giới Kei thay đổi đáng kể. Sợ hãi và không biết những gì đang xảy ra, Kei được cứu bởi người bạn của mình, Kai. Cùng nhau, họ chạy trốn sâu vào một ngọn núi hoang vắng. Sau đó, một nhóm các những người thù địch chống lại con người tiếp xúc Kai ... Ai là ngươi cần chiến đấu chống lại? Ai là Người mà anh cần theo phe giúp sức?\r\n', null, 'http://anime47.com/phim/ajin-demi-human/m5373.html', '[]', '2017-04-21 10:01:59', '2017-04-21 10:01:59');
