INSERT INTO `films` VALUES ('550', 'Asura Cryin 1', 'Tựa đề: Asura Cryin`\r\nTựa đề khác: Asura Crying\r\nTựa đề kanji: アスラクライン\r\nThể loại: TV Series\r\nSố tập: 13\r\nThời gian khởi chiếu: 02.04.2009 đến 25.06.2009\r\nThể loại: Hài hước, Shounen, Harem, Siêu năng lực…\r\nSản xuất: Gou Nakanishi, Hiroyuki Yuzawa, Osamu Uemura\r\nNội dung:\r\nĐược dựa theo bộ light novel của Gakuto Mikumo và Nao Watanuki. Truyện \r\nkể về 1 cậu học sinh trung học có thể tiếp xúc được với hồn ma của cô \r\nbạn thân thuở nhỏ.', null, 'http://anime47.com/phim/asura-cryin-1/m1848.html', '[]', '2017-04-21 10:12:30', '2017-04-21 10:12:30');
