INSERT INTO `films` VALUES ('665', 'Berserk: The Golden Age Arc III - The Advent [BD]', '\r\nPhim có những cảnh NTR Nhạy Cảm, Bạo Lực Nặng không phù hợp với trẻ vị thành niên. Khán giả nên cân nhắc trước khi xem. Với khán giả dưới 18 tuổi, hãy xem cùng phụ huynh để có những chỉ dẫn kỹ càng nhất.\r\n\r\nBerserk (ベルセルク) là bộ truyện tranh giả tưởng do Kentaro Miura vẽ và viết kịch bản.\r\n\r\nCốt truyện\r\n\r\nBối cảnh của bộ truyện được đặt trong 1 thế giới thần thoại vô cùng đen tối, ở đó mỗi con người khi sinh ra đều mang một sứ mệnh và vai trò nhất định. Đặc biệt, có những người được xem là có khả năng thay đổi thế giới, họ được gọi là God Hand và có quyền phán xét mọi việc. Đến một lúc nào đó người mang số mệnh phải đứng trước sự lựa chọn cho cuộc đời mình: đi theo số phận sắp đặt trở thành thành viên của God Hand hay tự tìm cho mình lối đi riêng.\r\n\r\nNhân vật chính của chúng ta là Gatts, hay Black Swordsman, người đang trong cuộc hành trình đi tìm lối thoát và trả thù Griffith, người bạn thân nhất, người thủ lĩnh đã bán đứng toàn bộ quân đội cho ác quỷ để đạt được giấc mơ của mình. Gatts và người yêu của anh, Caska, chịu 1 lời nguyền như vật  hiến tế cho Griffith và luôn bị săn lùng bởi vô số ác quỷ. \r\n\r\nChiến đấu liên tục không ngừng nghỉ, Gatts với một sức mạnh phi thường từ tuổi thơ khắc nghiệt và những năm tháng trên chiến trường, vung thanh gươm Dragon khổng lồ xẻ nát những gì cản đường anh, để bảo vệ người yêu, để trả thù và để sống sót.  \r\n', null, 'http://anime47.com/phim/berserk-the-golden-age-arc-iii-the-advent/m3306.html', '[]', '2017-04-21 10:19:05', '2017-04-21 10:19:05');
INSERT INTO `films` VALUES ('666', 'Beyblade Burst', 'Beyblade Burst là tên của đồ chơi Beyblade thế hệ thứ 3 mà Takara Tomy sẽ ra mắt tại Nhật Bản vào tháng 7 năm 2015. Takara Tomy cũng đồng thời công bố một bộ anime đang trong quá trình hoàn thiện và chuẩn bị lên sóng trong mùa xuân năm 2016.', null, 'http://anime47.com/phim/beyblade-burst/m5556.html', '[]', '2017-04-21 10:19:07', '2017-04-21 10:19:07');
