INSERT INTO `films` VALUES ('680', 'Binbo Danshi (Anh chàng nghèo khó)', 'Koyama Kazumi là một sinh viên nghèo nhưng anh luôn lạc quan và luôn tin rằng trên đời này có những thứ còn quý giá hơn tiền bạc. Dù rất nghèo nhưng anh vẫn gánh những món nợ khác của mọi người và chấp nhận đối mặt với nhiều thử thách để trả được những món nợ đó. ', null, 'http://anime47.com/phim/binbo-danshi-anh-chang-ngheo-kho/m4506.html', '[]', '2017-04-21 10:19:52', '2017-04-21 10:19:52');
