INSERT INTO `films` VALUES ('729', 'Boku dake ga Inai Machi - Thị Trấn Nơi Chỉ Mình Tôi Lưu Lạc', '\r\nFujinuma Satoru là một họa sĩ manga có khát vọng nhưng bản thảo của anh luôn bị từ chối, và bị nói cậu chưa đặt đủ cái tôi vào trong đó. Anh sống qua ngày bằng việc làm nhân viên giao bánh pizza. Một ngày, khi đang đi giao pizza, anh bỗng nhận thấy gì đó và bị vướng vào tai nạn...\r\n', null, 'http://anime47.com/phim/boku-dake-ga-inai-machi-thi-tran-noi-chi-minh-toi-luu-lac/m5374.html', '[]', '2017-04-21 10:22:36', '2017-04-21 10:22:36');
INSERT INTO `films` VALUES ('730', 'Boku no Hero Academia', 'Vào tương lai, lúc mà con người với những sức mạnh siêu nhiên là điều thường thấy quanh thế giới. Đây là câu chuyện về Izuku Midoriya, từ một kẻ bất tài trở thành một siêu anh hùng. Tất cả ta cần là mơ ướ...', null, 'http://anime47.com/phim/boku-no-hero-academia/m5519.html', '[]', '2017-04-21 10:22:38', '2017-04-21 10:22:38');
