INSERT INTO `films` VALUES ('744', 'Bokurano', 'Mô tả trong một kỳ nghỉ hè, một nhóm 15 trẻ em phát hiện một người đàn ông bí ẩn sống trong một hang động bờ biển, cùng với các tiện ích của công nghệ cao.Người đàn ông tuyên bố là một nhà phát triển game tạo ra một trò chơi video với một robot khổng lồ bảo vệ Trái đất từ 15 kẻ xâm lược ngoài hành tinh...', 'http://i.imgur.com/y97cIZf.jpg', 'http://anime47.com/phim/bokurano/m1284.html', '[]', '2017-04-21 10:24:34', '2017-04-21 10:24:34');
