INSERT INTO `films` VALUES ('747', 'Bonobono (2016)', 'Bono Bono, gag manga 4-koma của tác giả Mikio Igarashi sẽ có TV anime đầu tiên sau hai thập kỷ. Số tháng 2 của tạp chí Manga Life đã công bố rằng anime sẽ bắt đầu chiếu từ ngày 2/4/2016. Eiken (Sazae-san, Musashi no Ken) sẽ sản xuất anime này.\r\n\r\nManga xoay quanh các câu chuyện hài của rái cá Bono Bono, sóc Shimarisu, chồn Araiguma và những con vật khác.', 'http://i.imgur.com/jGoOAOx.jpg', 'http://anime47.com/phim/bonobono-2016/m5569.html', '[]', '2017-04-21 10:24:50', '2017-04-21 10:24:50');
