INSERT INTO `films` VALUES ('761', 'Brave Witches: Petersburg Daisenryaku', 'Tập phim này diễn ra giữa tập 7 và tập 8 và sẽ được chiếu tại các rạp vào ngày 13 tháng 5 năm 2017. Tập phim này cũng sẽ được phát hành cùng với khối lượng đĩa Blu-ray và DVD thứ bảy của anime, phát hành vào ngày 25 tháng 8 năm 2017.', 'http://i.imgur.com/xAD8u5T.jpg', 'http://anime47.com/phim/brave-witches-petersburg-daisenryaku/m6227.html', '[]', '2017-04-21 10:26:00', '2017-04-21 10:26:00');
