INSERT INTO `films` VALUES ('769', 'Bubuki Buranki: Hoshi no Kyojin', 'Khi Azuma Ikki trở về Nhật Bản sau 10 năm, anh bị tấn công bởi một nhóm người có vũ trang và trở thành tù nhân của họ. Kogane Asabuki, một người bạn thời thơ ấu, cứu anh với một vũ khí trên tay phải của cô được biết đến như Bubuki; một vũ khí với suy nghĩ riêng. Azuma Ikki biết về sự tồn tại của Bubuki và đi trên một cuộc hành trình cùng với những người bạn đồng hành', 'http://i.imgur.com/4zspYNZ.jpg', 'http://anime47.com/phim/bubuki-buranki-hoshi-no-kyojin/m5859.html', '[]', '2017-04-21 10:26:36', '2017-04-21 10:26:36');
