INSERT INTO `films` VALUES ('771', 'Buddy Complex', 'Câu truyện kể về Aoba Watase, một cậu học sinh theo học tại một trường ở Tokyo và sống một cuộc sống bình thường. Vào buổi sáng hôm khai giảng sau kỳ nghỉ hè, cậu tới trường như thường lệ. Và cậu vô tình gặp Jyunjyou Dio Weinberg . Cuộc gặp này sẽ sớm làm thay đổi vận mệnh thế giới.', null, 'http://anime47.com/phim/buddy-complex/m3791.html', '[]', '2017-04-21 10:26:51', '2017-04-21 10:26:51');
