INSERT INTO `films` VALUES ('780', 'Buppu na Mainichi', 'Câu chuyện sẽ tập trung vào một con mèo bắt đầu sống với 10 con chó, và cuộc sống hàng ngày bận rộn của động vật. Câu chuyện rút ra được cảm hứng từ ca sĩ Noriyuki Makihara và thú cưng của anh ta. \r\n(Nguồn: ANN)', 'http://i.imgur.com/2G8iNGB.jpg', 'http://anime47.com/phim/buppu-na-mainichi/m6206.html', '[]', '2017-04-21 10:27:19', '2017-04-21 10:27:19');
