INSERT INTO `films` VALUES ('834', 'Công Chúa Tóc Mây - Tangled 2010 [hd]', 'Công chúa tóc mây (Tangled) kể về Rapunzel tóc dài sống từ nhỏ tới lớn trên một tòa tháp cao. Nhưng khi tình cờ đụng độ một tên cướp và phải lòng chàng, đó là lần đầu tiên công chúa bước ra thế giới bên ngoài để tìm người yêu.', null, 'http://anime47.com/phim/cong-chua-toc-may-tangled-2010/m3065.html', '[]', '2017-04-21 10:31:07', '2017-04-21 10:31:07');
