INSERT INTO `films` VALUES ('1022', 'Dance with Devils', 'Mới đây, trang chủ của anime truyền hình Dance with Devils (Khiêu Vũ Cùng Bầy Quỷ) đã đăng tải đoạn video quảng bá cho bộ phim. Đi cùng với video là ca khúc mở đầu “Kakusei no Air” (Giai điệu của sự thức tỉnh) do ca sĩ Wataru Hatano trình bày.\r\n\r\nNgoài ra, nhóm Pentacle★ gồm những nam seiyuu chính như Souma Saitou, Hatano, Takashi Kondo, Subaru Kimura, và Daisuke Hirakawa cũng sẽ trình bày ca khúc kết thúc mang tên “Mademo★iselle”.\r\n\r\n\r\nDàn nhân sự gồm:\r\n– Đạo diễn: Ai Yoshimura (Yahari Ore no Seishun Rabu Kome wa Machigatteiru., Aoi Haru Ride)\r\n– Studio: Brains Base (Natsume Yuujinchou, Isshuukan Friends)\r\n– Kịch bản: Tomoko Konparu (Uta no Prince-sama – Maji Love 1000%, Aoi Haru Ride)\r\n– Ý tưởng và nguyên tác: Grimoire Henshuushitsu, Daisuke Iwasaki (Reject)\r\n– Âm nhạc: Elements Garden, Junpei Fujita (Elements Garden), DIVE II Entertainment, Avex\r\n– Thiết kế nhân vật: Hirotaka Maeda (Reject), Yuka Takashina', null, 'http://anime47.com/phim/dance-with-devils/m5255.html', '[]', '2017-04-21 10:40:59', '2017-04-21 10:40:59');
