INSERT INTO `films` VALUES ('1063', 'Boku, Otaryman.', 'Yoshitani l&agrave; một người l&agrave;m c&ocirc;ng ăn lương b&igrave;nh thường người Nhật sống một m&igrave;nh trong một th&agrave;nh phố lớn. Anh ấy phải đối mặt với những c&ocirc;ng việc tại c&ocirc;ng ty v&agrave; việc nh&agrave; m&igrave;nh tất cả từ một quan điểm otaku.', null, 'http://vuighe.net/boku-otaryman', '[1,3]', '2017-04-21 11:07:26', '2017-04-21 11:07:26');
