INSERT INTO `films` VALUES ('1066', 'Bungou Stray Dogs', '&lt;p&gt;Một ng&agrave;y kia, Nakajima Atsushi bị đ&aacute; đ&iacute;t ra khỏi c&ocirc; nhi viện, kh&ocirc;ng c&ograve;n nơi n&agrave;o để đi cũng như kh&ocirc;ng c&oacute; g&igrave; để ăn. L&uacute;c gần như chết đ&oacute;i, anh t&igrave;nh cờ cứu sống được một thằng cha cuồng tự tử ở bờ s&ocirc;ng t&ecirc;n l&agrave; Dazai Osamu. Người n&agrave;y v&agrave; cộng sự của anh ta l&agrave; Kunikida l&agrave; th&agrave;nh vi&ecirc;n của một tổ chức th&aacute;m tử đặc biệt. Họ c&oacute; những năng lực si&ecirc;u nhi&ecirc;n chuy&ecirc;n để đối ph&oacute; với những vụ &aacute;n v&ocirc; c&ugrave;ng nguy hiểm của cảnh s&aacute;t v&agrave; qu&acirc;n đội. Trong khoảng thời gian Atsushi đến th&agrave;nh phố n&agrave;y, họ cũng đang theo d&otilde;i một con hổ mới xuất hiện gần đ&acirc;y&nbsp;ở nơi đ&oacute;. Điều n&agrave;y dường như c&oacute; li&ecirc;n quan đặc biệt với Atsushi v&agrave; khi mọi chuyện đ&atilde; được giải quyết xong xu&ocirc;i, Atsushi buộc phải gắn b&oacute; với Dazai v&agrave; đội th&aacute;m tử b&iacute; ẩn đ&oacute; nhiều hơn nữa&hellip;&lt;/p&gt;&lt;p&gt;Xem th&ocirc;ng tin anime tại đ&acirc;y&nbsp;&lt;a href=&quot;http://tinanime.com/bungou-stray-dog-mot-trong-nhung-cai-ten-duoc-san-don-hang-dau-trong-mua-thu-nay&quot; target=&quot;_blank&quot;&gt;http://tinanime.com/bungou-stray-dog-mot-trong-nhung-cai-ten-duoc-san-don-hang-dau-trong-mua-thu-nay&lt;/a&gt;&lt;/p&gt;', null, 'http://vuighe.net/bungou-stray-dogs', '[4]', '2017-04-21 11:07:32', '2017-04-21 11:07:32');
